import React, { useEffect, useState } from 'react';
import axios from 'axios';
import * as d3 from 'd3';
import '../styles/Reports.css';

const Reports = () => {

  // eslint-disable-next-line no-unused-vars
const [data, setData] = useState([]);


  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem('token'); // Retrieve the token
      try {
        const response = await axios.get('http://localhost:5000/api/solar-generation', {
          headers: {
            Authorization: `Bearer ${token}`, // Include the token in headers
          },
        });
        setData(response.data);
        drawChart(response.data);
      } catch (error) {
        console.error("Error fetching the data:", error);
      }
    };
    fetchData();
  }, []);

const drawChart = (data) => {
  // Clear any previous chart
  d3.select('#chart').selectAll('*').remove();

  const width = 700;  // Chart width
  const height = 500; // Chart height
  const radius = Math.min(width, height) / 2;

  const color = d3.scaleOrdinal(d3.schemeCategory10);

  // Create SVG
  const svg = d3.select('#chart')
      .append('svg')
      .attr('width', width)
      .attr('height', height)
      .append('g')
      .attr('transform', `translate(${width / 2}, ${height / 2})`);

  // Tooltip
  const tooltip = d3.select('#chart')
      .append('div')
      .attr('class', 'tooltip')
      .style('opacity', 0)
      .style('position', 'absolute')
      .style('background', '#333')
      .style('color', '#fff')
      .style('padding', '5px')
      .style('border-radius', '5px')
      .style('font-size', '0.8rem');

  const pie = d3.pie().value(d => d.generation);
  const arc = d3.arc().innerRadius(0).outerRadius(radius);

  // Create arcs
  const arcs = svg.selectAll('.arc')
      .data(pie(data))
      .enter().append('g')
      .attr('class', 'arc');

  // Draw pie slices
  arcs.append('path')
      .attr('d', arc)
      .attr('fill', d => color(d.data.state))
      .on('mouseover', function (event, d) {
          tooltip.transition().duration(200).style('opacity', 0.9);
          tooltip.html(`${d.data.state}: ${d.data.generation} GWh`)
              .style('left', (event.pageX + 10) + 'px')
              .style('top', (event.pageY - 25) + 'px');
      })
      .on('mousemove', function (event) {
          tooltip.style('left', (event.pageX + 10) + 'px')
              .style('top', (event.pageY - 25) + 'px');
      })
      .on('mouseout', function () {
          tooltip.transition().duration(500).style('opacity', 0);
      });

  // Draw text labels inside the slices (with rotation for small slices)
  arcs.append('text')
      .attr('transform', d => {
          const centroid = arc.centroid(d);
          const angle = (d.startAngle + d.endAngle) / 2;
          const rotate = angle * 180 / Math.PI - 90;
          return `translate(${centroid}) rotate(${rotate})`;
      })
      .attr('dy', '.35em')
      .style('text-anchor', 'middle')
      .text(d => d.data.state)
      .style('font-size', '0.8rem')  // Adjust font size to fit within the slice
      .style('font-weight', 'bold')
      .style('fill', '#fff');  // White text for better contrast

  // Draw legend with more space from the chart
  const legend = svg.append('g')
      .attr('class', 'legend')
      .attr('transform', `translate(${width / 2 + radius + 20}, 0)`);  // Increased space between chart and legend

  data.forEach((d, i) => {
      const legendRow = legend.append('g')
          .attr('class', 'legend-row')
          .attr('transform', `translate(0, ${i * 20})`);

      legendRow.append('rect')
          .attr('width', 15)
          .attr('height', 15)
          .attr('fill', color(d.state));

      legendRow.append('text')
          .attr('x', 20)
          .attr('y', 12)
          .text(d.state)
          .style('font-size', '0.9rem')
          .style('color', '#333');
  });
};

  

  return (
    <div>
      <h1>Solar Energy Generation in 2023 by State (GWh)</h1>
      <div id="chart"></div>
      <p>This chart displays the solar energy generation in gigawatt-hours (GWh) for various states in the USA during 2023. The data reflects the total solar energy generated in each state, providing insights into the distribution of solar energy resources across the country.</p>
      <p>Source: <a href="https://www.climatecentral.org/report/solar-and-wind-power-2024" target="_blank" rel="noopener noreferrer">Climate Central</a></p>
    </div>
  );
};

export default Reports;
