import React from 'react';
import '../styles/dashboard.css';

const Dashboard = () => {
  return (
    <main className="container center" aria-labelledby="dashboard-title">
    
      
        
    <div className="section-container">
      <article className="section-box" aria-labelledby="summary-title">
        <h2 id="summary-title">Summary</h2>
        <p>
          Recent innovations in clean energy within the United States have focused on advancing solar and wind technologies, battery storage, and grid optimization. Breakthroughs in perovskite solar cells and offshore wind turbines have increased efficiency, reduced costs, and enhanced scalability. Battery storage solutions are also becoming more advanced, allowing for better integration of renewable energy into the power grid. Such developments are essential in meeting the country's carbon reduction goals and achieving a sustainable energy future.
        </p>
        <p>
          For more details, refer to the full report at <a href="https://www.climatecentral.org/report/solar-and-wind-power-2024" target="_blank" rel="noopener noreferrer">Climate Central</a>.
        </p>
      </article>

      <section className="section-box" aria-labelledby="tech-stack-title">
        <h2 id="tech-stack-title">Technical Aspects</h2>
        <p>
          The project utilizes MongoDB for data storage, Express and Node.js for server-side logic, and React for the frontend. JSON Web Tokens (JWT) handle secure authentication, and NGINX manages server requests. Accessibility features are implemented to meet ADA/WCAG standards, while interactive charts and data visualizations are incorporated using d3.js, presenting a user-friendly view of recent clean energy data.
        </p>
      </section>
    </div>

      

      <footer className="footer" role="contentinfo">
        &copy; 2023 Clean Energy Innovations Dashboard
      </footer>
    </main>
  );
};

export default Dashboard;
