// src/pages/Summary.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import * as d3 from 'd3';
import '../styles/Summary.css';

const Summary = () => {
    // eslint-disable-next-line no-unused-vars
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem('token'); // Retrieve the token
      try {
        const response = await axios.get('http://localhost:5000/api/monthly-solar-generation', {
          headers: {
            Authorization: `Bearer ${token}`, // Attach token to request headers
          },
        });
        setData(response.data);
        drawChart(response.data);
      } catch (error) {
        console.error('Error fetching the data:', error);
      }
    };
    fetchData();
  }, []);

  const drawChart = (data) => {
    // Clear any previous chart
    d3.select('#barChart').selectAll('*').remove();

    const width = 700;
    const height = 400;
    const margin = { top: 20, right: 20, bottom: 60, left: 60 };

    const svg = d3.select('#barChart')
      .append('svg')
      .attr('width', width)
      .attr('height', height);

    const xScale = d3.scaleBand()
      .domain(data.map(d => d.Month))
      .range([margin.left, width - margin.right])
      .padding(0.1);

    const yScale = d3.scaleLinear()
      .domain([0, d3.max(data, d => d.generation) * 1.1]) // Adjust to fit the highest value
      .range([height - margin.bottom, margin.top]);

    // Add bars
    svg.selectAll('.bar')
      .data(data)
      .enter()
      .append('rect')
      .attr('class', 'bar')
      .attr('x', d => xScale(d.Month))
      .attr('y', d => yScale(d.generation))
      .attr('width', xScale.bandwidth())
      .attr('height', d => height - margin.bottom - yScale(d.generation))
      .attr('fill', '#4e79a7')
      .on('mouseover', (event, d) => {
        tooltip.style('visibility', 'visible')
               .text(`${d.generation} GWh`);
      })
      .on('mousemove', (event) => {
        tooltip.style('top', (event.pageY - 10) + 'px')
               .style('left', (event.pageX + 10) + 'px');
      })
      .on('mouseout', () => tooltip.style('visibility', 'hidden'));

    // Add axes
    svg.append('g')
      .attr('transform', `translate(0,${height - margin.bottom})`)
      .call(d3.axisBottom(xScale))
      .selectAll('text')
      .attr('transform', 'rotate(-45)')
      .style('text-anchor', 'end');

    svg.append('g')
      .attr('transform', `translate(${margin.left},0)`)
      .call(d3.axisLeft(yScale).ticks(6).tickFormat(d => `${d} GWh`));

    // Add a tooltip
    const tooltip = d3.select('body').append('div')
      .attr('class', 'tooltip')
      .style('position', 'absolute')
      .style('visibility', 'hidden')
      .style('background-color', 'white')
      .style('border', '1px solid #ccc')
      .style('padding', '5px')
      .style('border-radius', '5px');

    // Calculate and add trendline
    const trendlineData = calculateTrendline(data);

    const line = d3.line()
      .x(d => xScale(d.Month) + xScale.bandwidth() / 2)
      .y(d => yScale(d.trend));

    svg.append('path')
      .datum(trendlineData)
      .attr('fill', 'none')
      .attr('stroke', 'orange')
      .attr('stroke-width', 2)
      .attr('d', line);
  };

  // Function to calculate the trendline data points
  const calculateTrendline = (data) => {
    const n = data.length;
    const xMean = d3.mean(data, (d, i) => i);
    const yMean = d3.mean(data, d => d.generation);

    let num = 0;
    let den = 0;
    data.forEach((d, i) => {
      num += (i - xMean) * (d.generation - yMean);
      den += (i - xMean) ** 2;
    });

    const slope = num / den;
    const intercept = yMean - slope * xMean;

    return data.map((d, i) => ({
      Month: d.Month,
      trend: intercept + slope * i,
    }));
  };

  return (
    <div className="summary-container">
      <h1>Monthly Solar Energy Generation in the U.S. (2023)</h1>
      <div id="barChart"></div>
      <p>
        This chart illustrates the monthly solar energy generation in gigawatt-hours (GWh) across the United States in 2023.
        The data provides insights into seasonal trends in solar energy production.
        Source: <a href="https://www.climatecentral.org/report/solar-and-wind-power-2024" target="_blank" rel="noopener noreferrer">Climate Central</a>
      </p>
    </div>
  );
};

export default Summary;
